sys_variable_value.value:
(function execute(inputs, outputs) {
    var availablePto = inputs.employeeInfo.available_pto_days;
    var used_aoe = inputs.employeeInfo.used_aoe_days;
    var used_sick_leave = inputs.employeeInfo.used_sick_leave_days;
    var type = inputs.timeOffType;

    outputs.available_pto = availablePto;
    outputs.used_aoe = used_aoe;
    outputs.out_used_sick_leave = used_sick_leave;

    if (inputs.approved == "approved") {
        if (type == "pto") {
            outputs.available_pto = availablePto - 1;
        } else if (type == "aoe") {
            outputs.used_aoe = used_aoe + 1;
        } else if (type == "sick_leave") {
            outputs.out_used_sick_leave = used_sick_leave + 1;
        }
    }
})(inputs, outputs);

