sys_script.script:
(function executeRule(current, previous) {

	var fromDate = current.from.getNumericValue();
	var toDate = current.to.getNumericValue();

	var dateDiff = toDate - fromDate;
	var daysDiff = dateDiff / (24 * 60 * 60 * 1000);

	gs.info("DAYS DIFF: " + dateDiff);
	gs.info("DAYS DIFF: "+ daysDiff);


	var employeeGr = new GlideRecord("x_1122522_pfc_employee_info");
	employeeGr.addQuery("employee", current.requested_by);
	employeeGr.query();

	gs.info(employeeGr);

	if (employeeGr.next()) {
		var fromDate = current.from.getNumericValue();
		var toDate = current.to.getNumericValue();

		var dateDiff = toDate - fromDate;
		var daysDiff = dateDiff / (24 * 60 * 60 * 1000);

		gs.info("DAYS DIFF: " + dateDiff);
		gs.info("DAYS DIFF: "+ daysDiff);

		if (employeeGr.getValue("available_pto_days") <= "0") {
			gs.addErrorMessage("You don't have available PTO days");
			current.setAbortAction(true);
		} else if (daysDiff > employeeGr.getValue("available_pto_days")) {
			gs.addErrorMessage("Available PTO days are not enough");
			current.setAbortAction(true);
		}
	}

})(current, previous);
