sysauto_script.script:
var today = new GlideDateTime();
var timeOffGr = new GlideRecord('x_1122522_pfc_time_off');
//timeOffGr.addQuery('to','<=',today);

timeOffGr.query();
gs.info("COUNT: " + timeOffGr.getRowCount());

while(timeOffGr.next()){
	gs.info("\n");
	gs.info("Requested By = " + timeOffGr.getValue("requested_by"));
	var userGr = new GlideRecord("sys_user");
	if (timeOffGr.getValue("requested_by")) {
		userGr.addQuery("sys_id", timeOffGr.getValue("requested_by"));
		userGr.query();


		if (userGr.next()) {
			gs.info("Requested By Record: " + userGr.getValue("x_1122522_pfc_employee_info"));

			var employeeInfoGr = new GlideRecord("x_1122522_pfc_employee_info");
			employeeInfoGr.addQuery("sys_id", userGr.getValue("x_1122522_pfc_employee_info"));
			employeeInfoGr.query();

			if (employeeInfoGr.next()) {
				gs.info("Is Vacationing " + employeeInfoGr.getValue("is_vacationing"));
				employeeInfoGr.setValue("is_vacationing", false);
				employeeInfoGr.update();
			}
		}
	}	
}
